import React from "react";
import { Link } from "react-router-dom";
import { Button, Form, FormGroup, Label, Input, FormText } from "reactstrap";

import Select from "@atlaskit/select";
// import Button from "@atlaskit/button";

// import Form, {
//   Field,
//   FieldGroup,
//   FormHeader,
//   FormSection,
//   FormFooter
// } from "@atlaskit/form";

import "./lhs.css";

export default class LHS extends React.Component {
  constructor(props) {
    super(props);

    // Set initial state
    this.state = {
      count: ""
    };

    this.handleChange = this.handleChange.bind(this);
    this.submitTest = this.submitTest.bind(this);
  }

  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  submitTest() {
    let params = {
      count: this.state.count
    };

    this.props.runTest(params);
  }

  render() {
    return (
      <div className="col-md-4 lhs">
        {/* <div className="title">
          <h5>Configure test case</h5> 
          Configure test case
        </div> */}

        <div className="body">
          <h3>
            <i className="fas fa-cogs" /> Configure fractal generation
          </h3>

          <FormGroup>
            <Label>Image size</Label>
            <Input
              type="select"
              name="size"
              className="at-select"
              value={this.state.size}
              onChange={this.handleChange}
            >
              <option value="" disabled selected>
                Select...
              </option>
              <option>100x100</option>
              <option>200x200</option>
              <option>300x300</option>
              <option>400x400</option>
            </Input>
          </FormGroup>

          <FormGroup>
            <Label>Number of nodes</Label>
            <Input
              type="select"
              name="count"
              className="at-select"
              value={this.state.count}
              onChange={this.handleChange}
            >
              <option value="" disabled selected>
                Select...
              </option>
              <option>1</option>
              <option>2</option>
              <option>3</option>
              <option>4</option>
            </Input>
          </FormGroup>

          {/* <Field label="Number of threads">
            <Select />
          </Field>

          <Field label="Iterations">
            <Select />
          </Field>

          <Field label="Threshold">
            <Select />
          </Field> */}

          <br />
          <div
            className="btn btn-lg btn-primary w-100 text-left"
            onClick={this.submitTest}
          >
            Generate fractals <i className="fas fa-arrow-right fa-sm ml-2" />
          </div>
        </div>

        {/* <div>pis, threads, iterations, threashold</div> */}

        {/* <Field label="Field 1"> */}

        {/* </Field> */}
      </div>
    );
  }
}
