import React from "react";
import { FormGroup, Input } from "reactstrap";

import Dropdown from "./Dropdown.jsx";

export default class SingleForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      size: 100,
      iterations: 10,
      nodes: 1,
      threads: 1
    };

    this.handleChange = this.handleChange.bind(this);
    this.submitSingleTest = this.submitSingleTest.bind(this);
  }

  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  submitSingleTest() {
    let params = {
      type: "single",
      size: this.state.size,
      iterations: this.state.iterations,
      nodes: this.state.nodes,
      threads: this.state.threads
    };

    this.props.submitTest(params);
  }

  render() {
    return (
      <div className="w-100 body">
        <h3>
          <i className="far fa-image" /> Configure single test
        </h3>
        <FormGroup>
          <small>Image size</small>
          <Input
            type="select"
            name="size"
            className="at-select"
            value={this.state.size}
            onChange={this.handleChange}
          >
            <option value={100}>100x100</option>
            <option value={200}>200x200</option>
            <option value={300}>300x300</option>
            <option value={400}>400x400</option>
          </Input>
        </FormGroup>

        <FormGroup>
          <small>Max iterations</small>
          <Input
            type="select"
            name="iterations"
            className="at-select"
            value={this.state.iterations}
            onChange={this.handleChange}
          >
            <option>10</option>
            <option>20</option>
            <option>50</option>
            <option>75</option>
            <option>100</option>
          </Input>
        </FormGroup>

        <Dropdown
          name="nodes"
          title={"Number of nodes"}
          handleChange={this.handleChange}
          value={this.state.nodes}
          options={[1, 2, 3, 4]}
        />

        <Dropdown
          name="threads"
          title={"Threads per node"}
          handleChange={this.handleChange}
          value={this.state.threads}
          options={[1, 2, 3, 4]}
        />

        <br />
        <div
          className="btn btn-lg btn-primary w-100 text-left"
          onClick={this.submitSingleTest}
        >
          Generate fractal <i className="fas fa-arrow-right fa-sm ml-2" />
        </div>
      </div>
    );
  }
}
