import React from "react";
import { Link } from "react-router-dom";
import { Button, Form, FormGroup, Label, Input, FormText } from "reactstrap";

import Tabs from "@atlaskit/tabs";

import SingleForm from "./SingleForm.jsx";

// Styles
import "./lhs.css";

export default class LHS extends React.Component {
  constructor(props) {
    super(props);

    // Set initial state
    this.state = {
      size: 100,
      iterations: 10,
      nodes: 1,
      threads: 1
    };

    this.handleChange = this.handleChange.bind(this);
    this.submitTest = this.submitTest.bind(this);
  }

  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  submitTest() {
    let params = {
      size: this.state.size,
      iterations: this.state.iterations,
      nodes: this.state.nodes,
      threads: this.state.threads
    };

    this.props.runTest(params);
  }

  render() {
    return (
      <div className="col-md-4 lhs">
        {/* <div className="title">
          <h5>Configure test case</h5> 
          Configure test case
        </div> */}

        <div className="body">
          <h3>
            <i className="fas fa-cogs" /> Configure generation
          </h3>

          <Tabs
            tabs={[
              { label: "Tab 1", content: <SingleForm /> },
              { label: "Tab 2", content: <div>yo</div> }
            ]}
          />
        </div>

        {/* <div>pis, threads, iterations, threashold</div> */}

        {/* <Field label="Field 1"> */}

        {/* </Field> */}
      </div>
    );
  }
}
